import React, { Component } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faTimesCircle } from '@fortawesome/free-solid-svg-icons';

class Image extends Component {
  state = { value: '' };

  handleChange = evt => {
    this.setState({ value: evt.target.value }).then(
      this.props.handleChange(this.props.image.public_id, this.state)
    );
  };

  render() {
    return (
      <div key={this.props.id}>
        <div className="fadein">
          <div
            onClick={() => this.props.removeImage(this.props.image.public_id)}
            className="delete"
          >
            <FontAwesomeIcon icon={faTimesCircle} size="2x" />
          </div>
          <img
            src={this.props.image.secure_url}
            alt=""
            onError={() => this.props.onError(this.props.image.public_id)}
          />
        </div>
        <input
          type="text"
          placeholder="image description"
          onChange={() =>
            this.props.handleChange(
              this.props.image.public_id,
              this.props.image.value
            )
          }
        />
      </div>
    );
  }
}

export default Image;
